import React, { Component } from 'react'

export class Pollution extends Component {
  render() {
    const api = {
        key: "43fab9d64bf617e959ae5a762f12ed64",
        base: "https://api.openweathermap.org/data/2.5/",
    };
   const fetchWeather = async (e) => {
        const res = await fetch(`${api.base}weather?q=${this.cityName.value}&units=metric&APPID=${api.key}`)
        const data = await res.json();
        const lat = data.coord.lat
        const lon = data.coord.lon
        const res2 = await fetch(`${api.base}air_pollution?lat=${lat}&lon=${lon}&appid=${api.key}`)
        const data2 = await res2.json();

        console.log('DATA',data2);
    };
    return (
        <>
        <div className='pollution normal'>
            <div className='input-form '>
                <form className='polluteform'>
                    <input type="text" placeholder='City Name' ref={(input)=>{ this.cityName = input }}/>
                    <input type='submit' onKeyPress={fetchWeather} />
                </form>
            </div>
        </div>
        </>
    )
  }
}

export default Pollution
