    constructor() {
        const [query, setQuery] = useState("");

        handleChange(e); {
            this.setQuery(e.target.value);
            const fetchWeather = async (e) => {
                if (e.key === "Enter") {
                    const res = await fetch(`${WEATHER_API_URL}weather?q=${query}&units=metric&APPID=${WEATHER_API_KEY}`);
