                            <input type="text" placeholder='City Name' onChange={(e) => { this.handleChange(e); } } ref={(input) => this.myinput = input}
                                value={query} />
                        </form>
                    </div>
                </div>
            </>
        );
    }
}
export default PollutionPage;
